import React, { Component } from 'react'
import {Link, NavLink} from 'react-router-dom'

class SignIn extends Component {
  state = {
    email: '',
    password: ''
  }
  handleChange = (e) => {
    this.setState({
      [e.target.id]: e.target.value
    })
  }
  handleSubmit = (e) => {
    e.preventDefault();
    console.log(this.state);
  }
  render() {
    return (
      <div className="row">
        <div className="col-md-4" />
        <div className="col-md-3 px-0">
          <h4 className="text-center mt-5 mb-3"><strong>Sign in</strong></h4>
          <div className="card">
            <div className="card-body">
              <form className="px-4 py-4" onSubmit={this.handleSubmit}>
                <div className="form-group">
                  <input type="email" className="form-control" id="email" placeholder="Email" onChange={this.handleChange}/>
                </div>
                <div className="form-group">
                  <input type="password" className="form-control" id="password" placeholder="Password" onChange={this.handleChange}/>
                </div>
                <div className="form-group form-check">
                  <input type="checkbox" className="form-check-input" id="checkbox" />
                  <label className="form-check-label" htmlFor="checkbox">Remember me</label>
                </div>
                <button type="submit" className="btn btn-success float-right">Submit</button>
              </form>
              
            </div>
            <a href="#" >Not Signed in?</a>
          </div>
        </div>
      </div>
    )
  }
}

export default SignIn
